uto;}#customizable-info3{background-color:inherit;height:auto;width:100%;display:block;margin-right:auto;margin-left:auto;font-family:Roboto;color:rgb(42,42,42);font-size:24px;line-height:1.5;text-align:center;}#customizable-info{background-color:rgba(0,0,0,0.6);height:100vh;min-height:100vh;width:100%;display:table;position:relative;padding:10px;left:0px;right:0px;top:0px;bottom:0px;}.banner-text{font-size:36px;}@media(min-width:768px){.banner-text{font-size:46px;}}#fast-info,#fun-info,#customizable-info{display:none;}@keyframes shadow-in{from{box-shadow: 0px 0px 0px #eeeeee;margin-top:0;}to{box-shadow:0px 10px 0px gray;margin-top:-3px;}}@keyframes shadow-out{from{box-shadow: 0px 10px 0px gray;margin-top:-3px;}to{box-shadow:0px 0px 0px #eeeeee;margin-top:0;}}.shadowIn{animation-name: shadow-in;animation-duration: 200ms;animation-timing-function: ease-out;-webkit-animation-fill-mode: forwards;}.shadowOut{animation-name: shadow-out;animation-duration: 200ms;animation-timing-function: ease-out;-webkit-animation-fill-mode: forwards;}@media(min-width: 768px){#i7,#i42,#i1{width:100%;}.home-column{width:50%;}#i40,#i39{width:500px;}.info-box{width:100%;}#fast-info2{width:600px;}.more-info-text{width:100%;}}@media(min-width: 992px){#i7,#i42,#i1{width:97.5%;}#i3{width:85px;}.home-column{width:33.33%;}#fast-icon{width:200px;}#fun-icon{width:200px;}#customizable-icon{width:200px;}.info-box{width:70%;}.more-info-text{width:70%;}.get-started-btn{width:300px;}#fun-info2{width:250px;}#customizable-info2{width:250px;}}@media(min-width: 1200px){#i7,#i42,#i1{width:1170px;}}</style><link defer="https://fonts.googleapis.com/css?family=Roboto:400,700" rel="stylesheet" type="text/css"></head><body><div class="container"><div id="i0"><div id="i1"><div id="i2"><b>Triangle </b></div><div id="i3"><img src="data:image/gif;base64,R0lGODlhQABAAIAAAMXFxQAAACH5BAAAAAAALAAAAABAAEAAAAJFhI+py+0Po5y02ouz3rz7D4biSJbmiabqyrbuC8fyTNf2jef6zvf+DwwKh8Si8YhMKpfMpvMJjUqn1Kr1is1qt9yuF1AAADs=" lazyload="images