x.style.padding = "15px 30px"; box.style.position = "fixed"; box.style.top = box.style.right = "20px"; box.style.opacity = 0.8; box.style.color = "#ffffff"; box.style.borderRadius = "5px"; box.style.boxShadow = "0px 1px 3px #333333"; box.style.zIndex = "100"; box.innerHTML = "Added 1 to Cart"; document.body.appendChild(box); setTimeout(function(){ document.body.removeChild(box); }, 1500); }</script></body></html>