document.addEventListener("scroll",lazyload);function lazyload(){if(document.getElementById("myImages").style.display==="none"){return}var b=document.querySelectorAll("img[lazyload]");for(var a=0;a<b.length;a++){if(b[a].getBoundingClientRect().top<window.innerHeight+300){b[a].src=b[a].getAttribute("lazyload");b[a].removeAttribute("lazyload")}}}lazyload();